    public void sendMail(string to, string subject)
    {
        try
        {
            using (var msg = new MailMessage(Configuration["EmailFrom"], to))
            {
                msg.Subject = subject;
                msg.Body = this.emailBody;
                //msg.Attachments.Add(new Attachment("https://media.contentapi.ea.com/content/dam/apex-legends/images/2019/01/apex-featured-image-16x9.jpg.adapt.crop16x9.1023w.jpg"));
                msg.IsBodyHtml = true;
                msg.BodyEncoding = System.Text.Encoding.UTF8;
                SmtpClient smtpClient = new SmtpClient(Configuration["SMTP"], Convert.ToInt32(Configuration["PORT"]));
                System.Net.NetworkCredential credentials = new System.Net.NetworkCredential(Configuration["EmailFrom"], Configuration["PasswordEmailFrom"]);
                smtpClient.Credentials = credentials;
                smtpClient.EnableSsl = true;
                smtpClient.Send(msg);
            }
        }
        catch (Exception)
        {
        }
    }

    public void addAttacHment(string path)
    {

        string oPath = Path.Combine(webRoot, path);

        Attachment inline = new Attachment(oPath);
        inline.ContentDisposition.Inline = true;
        inline.ContentDisposition.DispositionType = DispositionTypeNames.Inline;
        inline.ContentId = "Logo";
        inline.ContentType.MediaType = "image/png";
        inline.ContentType.Name = Path.GetFileName(oPath);
        this.attacHments.Add(inline);
        inline.Dispose();
    }
